<?php  /**  * Run all unit tests found in this folder  */    // App path passed as arg  if( sizeof( $argv ) > 1 ){    echo "Setting APP_PATH to ".$argv[1]."\n";    define( "APP_PATH", realpath( $argv[1] ) );  }  $dir = dirname(__FILE__);  $dh = opendir( $dir );  while( $file = readdir( $dh ) ){    $file = trim( $file );    if( !preg_match( "/(.+)\.test\.class\.php$/", $file, $m ) ) continue;    require_once( $dir."/".$file );  }?>